import React from 'react';
import { useNavigate } from 'react-router-dom';

function DoctorHomepage() {
  const navigate = useNavigate();

  // Navigation handlers
  const handleViewAppointments = () => navigate('/appointments');
  const handlePatientRecords = () => navigate('/patient-records');
  const handleProfile = () => navigate('/profile');
  const handleLogout = () => {
    // Clear session storage or perform any cleanup
    sessionStorage.clear();
    navigate('/login');
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Doctor's Dashboard</h1>
      <div style={styles.cardContainer}>
        <div style={styles.card} onClick={handleViewAppointments}>
          <h2>View Appointments</h2>
          <p>Manage your upcoming appointments</p>
        </div>
        <div style={styles.card} onClick={handlePatientRecords}>
          <h2>Patient Records</h2>
          <p>Access and manage patient information</p>
        </div>
        <div style={styles.card} onClick={handleProfile}>
          <h2>Profile</h2>
          <p>View and update your profile</p>
        </div>
        <div style={styles.card} onClick={handleLogout}>
          <h2>Logout</h2>
          <p>Sign out of your account</p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#f9f9f9',
    minHeight: '100vh',
    padding: '20px',
    textAlign: 'center',
  },
  header: {
    color: '#333',
    marginBottom: '20px',
    fontSize: '32px',
  },
  cardContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '20px',
    padding: '20px',
  },
  card: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s, box-shadow 0.3s',
    cursor: 'pointer',
  },
  cardHover: {
    transform: 'scale(1.05)',
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.15)',
  },
};

export default DoctorHomepage;
